import React, { useEffect, useState } from 'react';

function RecommendationCatalog({ onAddToCart, onProductView }) {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchRecommendations = async () => {
            setLoading(true);
            setError('');
            try {
                const token = localStorage.getItem('token');
                const response = await fetch('http://localhost:8080/api/recommendations', {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (response.ok) {
                    const data = await response.json();
                    setProducts(data.data || []);
                } else {
                    setError('Không thể lấy danh sách gợi ý.');
                }
            } catch (err) {
                setError('Lỗi mạng khi lấy gợi ý.');
            }
            setLoading(false);
        };
        fetchRecommendations();
    }, []);

    if (loading) return <div>Đang tải gợi ý...</div>;
    if (error) return <div className="error-message">{error}</div>;
    if (!products.length) return <div>Không có sản phẩm gợi ý.</div>;

    return (
        <div className="product-catalog">
            <h2>Sản phẩm gợi ý cho bạn</h2>
            <div className="product-list">
                {products.map(product => (
                    <div key={product._id} className="product-card">
                        <img src={product.image_url} alt={product.name} onClick={() => onProductView(product._id)} />
                        <h3>{product.name}</h3>
                        <p>{product.description}</p>
                        <p>Giá: {product.price}₫</p>
                        <button onClick={() => onAddToCart(product._id, 1)}>Thêm vào giỏ</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default RecommendationCatalog;
