import React, { useState, useEffect, useRef } from 'react';
import ProductDetail from './ProductDetail'; // Import ProductDetail component
import './ProductCatalog.css';

function ProductCatalog({ onAddToCart, onProductView, userId }) {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState(''); // Presence of searchTerm dictates search mode
    const [message, setMessage] = useState('');
    const [categories, setCategories] = useState([]);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [sortOption, setSortOption] = useState('');
    // Removed isSearching state. It's now derived from searchTerm.
    const [selectedProductId, setSelectedProductId] = useState(null);

    const initialLoadRef = useRef(true); // Ref to track initial load for useEffect

    console.log("ProductCatalog re-render. searchTerm:", searchTerm, "selectedProductId:", selectedProductId);

    const fetchCategories = async () => {
        try {
            const response = await fetch('http://localhost:8080/api/products/categories');
            if (response.ok) {
                const data = await response.json();
                setCategories(data);
            } else {
                console.error('Error fetching categories:', response.statusText);
            }
        } catch (error) {
            console.error('Network error fetching categories:', error.message);
        }
    };

    const fetchProducts = async (term = '', category = '', sort = '', forRecommendation = false) => {
        try {
            let url;
            let currentProducts = [];
            // If forRecommendation is true OR searchTerm is empty, fetch recommendations
            // Otherwise, fetch search results
            if (forRecommendation || term.trim() === '') {
                if (!userId) {
                    setMessage('User ID not available for recommendations.');
                    setProducts([]);
                    return;
                }
                url = `http://localhost:8080/api/recommendations`;
                const response = await fetch(url, {
                    headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
                });
                if (response.ok) {
                    const data = await response.json();
                    currentProducts = data.data || [];
                    setMessage('Hiện không có gợi ý. Vui lòng xem thêm sản phẩm để nhận được gợi ý.'); // Default message
                    if (currentProducts.length > 0) {
                        setMessage(''); // Clear message if recommendations are found
                    }
                } else {
                    setMessage(`Error fetching recommendations: ${response.statusText}`);
                }
            } else { // It's a search term based request
                const params = new URLSearchParams();
                if (term) {
                    params.append('keyword', term);
                }
                if (category) {
                    params.append('category', category);
                }
                if (sort) {
                    params.append('sort', sort);
                }

                if (params.toString()) {
                    url = `http://localhost:8080/api/products/search?${params.toString()}`;
                } else {
                    url = 'http://localhost:8080/api/products/all';
                }

                const response = await fetch(url);
                if (response.ok) {
                    currentProducts = await response.json();
                    setMessage('');
                } else {
                    setMessage(`Error fetching products: ${response.statusText}`);
                }
            }
            setProducts(currentProducts);

        } catch (error) {
            setMessage(`Network error fetching products/recommendations: ${error.message}`);
        }
    };

    useEffect(() => {
        // Only run fetch on initial mount or when dependencies explicitly change
        if (initialLoadRef.current) {
            initialLoadRef.current = false;
            console.log("ProductCatalog useEffect initial load. Fetching recommendations.");
            fetchProducts('', '', '', true); // Initial load: fetch recommendations
        } else {
            console.log("ProductCatalog useEffect triggered. searchTerm:", searchTerm, "sortOption:", sortOption);
            // Re-fetch when searchTerm or sortOption changes, only if not viewing product detail
            if (selectedProductId === null) {
                if (searchTerm.trim() === '') {
                    fetchProducts('', '', '', true); // Back to recommendations if search is cleared or initial state
                } else {
                    fetchProducts(searchTerm, '', sortOption); // Fetch based on search term
                }
            }
        }
        fetchCategories();
    }, [userId, searchTerm, sortOption, selectedProductId]); // Removed isSearching as a dependency

    const handleSearchTermChange = (e) => {
        setSearchTerm(e.target.value);
        // No need to set isSearching here, it's derived from searchTerm's presence
    };

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        console.log("handleSearchSubmit called. Current searchTerm:", searchTerm);
        if (searchTerm.trim() === '') {
            // If search term is empty, go back to recommendations
            fetchProducts('', '', '', true);
        } else {
            // Otherwise, perform search
            fetchProducts(searchTerm, '', sortOption);
        }
        setShowSuggestions(false);
    };

    const handleCategoryClick = (categoryName) => {
        setSearchTerm(categoryName);
        fetchProducts('', categoryName, sortOption);
        setShowSuggestions(false);
    };

    const handleSortChange = (e) => {
        const newSortOption = e.target.value;
        setSortOption(newSortOption);
        // Always fetch based on current searchTerm and new sort option
        fetchProducts(searchTerm, '', newSortOption);
    };

    // Function to handle viewing a product detail
    const handleProductViewClick = (productId) => {
        console.log("handleProductViewClick called. Setting selectedProductId:", productId);
        setSelectedProductId(productId);
        onProductView(productId); // Call the prop function for tracking view
    };

    // Function to handle going back from product detail
    const handleBackToCatalog = () => {
        console.log("handleBackToCatalog called. Before setting selectedProductId to null. Current searchTerm:", searchTerm);
        setSelectedProductId(null);
        // useEffect will handle re-fetching based on the current searchTerm
        console.log("handleBackToCatalog: After setting selectedProductId to null. ProductCatalog will re-render and re-fetch based on current state.");
    };

    // Conditionally render ProductDetail or ProductCatalog content
    if (selectedProductId) {
        return (
            <ProductDetail
                productId={selectedProductId}
                onBack={handleBackToCatalog}
                onAddToCart={onAddToCart}
            />
        );
    }

    return (
        <div className="product-catalog-container">
            <h2>{searchTerm.trim() !== '' ? "Kết quả tìm kiếm" : "Sản phẩm gợi ý cho bạn"}</h2>
            <form onSubmit={handleSearchSubmit} className="search-bar">
                <input
                    type="text"
                    placeholder="Tìm kiếm sản phẩm..."
                    value={searchTerm}
                    onChange={handleSearchTermChange}
                    onFocus={() => setShowSuggestions(true)}
                    onBlur={() => setTimeout(() => setShowSuggestions(false), 100)}
                />
                <button type="submit">Tìm kiếm</button>
            </form>

            {showSuggestions && categories.length > 0 && (
                <div className="category-suggestions">
                    <h3>Danh mục gợi ý:</h3>
                    <ul>
                        {categories.slice(0, 6).map(category => (
                            <li key={category} onClick={() => handleCategoryClick(category)}>
                                {category}
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {searchTerm.trim() !== '' && (
                <div className="sort-options">
                    <label htmlFor="sort">Sắp xếp theo:</label>
                    <select id="sort" value={sortOption} onChange={handleSortChange}>
                        <option value="">Mặc định</option>
                        <option value="priceAsc">Giá: Thấp đến Cao</option>
                        <option value="priceDesc">Giá: Cao đến Thấp</option>
                        <option value="newest">Mới nhất</option>
                        <option value="oldest">Cũ nhất</option>
                    </select>
                </div>
            )}

            {message && <p className="message error">{message}</p>}

            <div className="product-list">
                {products.length > 0 ? (
                    products.map(product => (
                        <div key={product._id} className="product-item">
                            <div
                                className="product-image-container"
                                style={product.image_url ? { backgroundImage: `url(${product.image_url})` } : {}}
                            >
                                {!product.image_url && <div className="no-image">Không có hình ảnh</div>}
                                {product.stock === 0 && (
                                    <img src="/soldout.png" alt="Sold Out" className="sold-out-overlay" />
                                )}
                            </div>
                            <h3>{product.name}</h3>
                            <p className="price">{product.price.toLocaleString('vi-VN')} VND</p>
                            <p className="stock">Tồn kho: {product.stock}</p>
                            <div className="actions">
                                <button onClick={() => handleProductViewClick(product._id)} className="secondary-btn">Xem chi tiết</button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Không tìm thấy sản phẩm nào.</p>
                )}
            </div>
        </div>
    );
}

export default ProductCatalog;
