import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddProduct from './AddProduct';
import './SellerProducts.css';

const SellerProducts = () => {
  const [products, setProducts] = useState([]);
  const [showAdd, setShowAdd] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchProducts = async () => {
    setLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:8080/api/products/GetAllProducts', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProducts(res.data);
    } catch (err) {
      setError('Không thể tải sản phẩm.');
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddSuccess = () => {
    setShowAdd(false);
    fetchProducts();
  };

  return (
    <div className="seller-products-container">
      <h2>Sản phẩm của bạn</h2>
      <button className="add-btn" onClick={() => setShowAdd(true)}>Thêm sản phẩm</button>
      {showAdd && <AddProduct onSuccess={handleAddSuccess} />}
      {loading ? (
        <p>Đang tải...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : (
        <div className="products-list">
          {products.map(product => (
            <div className="product-card" key={product.id}>
              <img src={product.imageUrl} alt={product.name} className="product-img" />
              <h3>{product.name}</h3>
              <p><strong>Giá:</strong> {product.price.toLocaleString()} VND</p>
              <p><strong>Số lượng:</strong> {product.stock}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SellerProducts;
