import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddProduct from './AddProduct';
import ProductDetail from './ProductDetail';
import './SellerProductList.css';

const SellerProductList = () => {
  const [productsByCategory, setProductsByCategory] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAdd, setShowAdd] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const fetchProducts = async () => {
    setLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:8080/api/products/GetAllProducts', {
        headers: { Authorization: `Bearer ${token}` },
      });
      // Group products by category
      const grouped = {};
      res.data.forEach(product => {
        const cat = product.category || 'Khác';
        if (!grouped[cat]) grouped[cat] = [];
        grouped[cat].push(product);
      });
      setProductsByCategory(grouped);
    } catch (err) {
      setError('Không thể tải sản phẩm.');
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddSuccess = () => {
    setShowAdd(false);
    fetchProducts();
  };

  return (
    <div className="seller-product-list-container">
      <h2>Danh mục sản phẩm của bạn</h2>
      {loading ? (
        <p>Đang tải...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : (
        Object.keys(productsByCategory).length === 0 ? (
          <p>Chưa có sản phẩm nào.</p>
        ) : (
          Object.entries(productsByCategory).map(([category, products]) => (
            <div key={category} className="category-section">
              <h3>{category}</h3>
              <div className="products-list">
                {products.map(product => (
                  <div className="product-card" key={product.id} onClick={() => setSelectedProduct(product)} style={{cursor:'pointer'}}>
                    <img src={product.imageUrl} alt={product.name} className="product-img" />
                    <h4>{product.name}</h4>
                    <p><strong>Giá:</strong> {product.price.toLocaleString()} VND</p>
                    <p><strong>Số lượng:</strong> {product.stock}</p>
                  </div>
                ))}
              </div>
            </div>
          ))
        )
      )}
      <div style={{marginTop: 32}}>
        {showAdd ? (
          <AddProduct onSuccess={handleAddSuccess} />
        ) : (
          <button className="add-btn" onClick={() => setShowAdd(true)}>
            Thêm sản phẩm
          </button>
        )}
      </div>
      {selectedProduct && (
        <ProductDetail product={selectedProduct} onClose={() => setSelectedProduct(null)} />
      )}
    </div>
  );
};

export default SellerProductList;
